// Ambil output mentah dari AI Agent
const raw = $json.output || "";

// Buang wrapping ```json ... ``` kalau ada
const cleaned = raw.replace(/```json|```/g, "").trim();

let parsed;
try {
  parsed = JSON.parse(cleaned);
} catch (e) {
  return [
    {
      json: {
        error: "Gagal parse AI output",
        raw: cleaned,
        message: e.message
      }
    }
  ];
}

// Ambil data dari webhook (kalau ada)
const webhookData = $('Webhook').all()[0]?.json?.body || {};

// Return object JSON yang valid
return [
  {
    json: {
      response: parsed.response || "",
      suggestions: parsed.suggestions || [],
      context: {
        ...parsed.context,
        session_id: webhookData.session_id || parsed.context?.session_id || "",
        user_id: webhookData.context?.user_id || parsed.context?.user_id || ""
      }
    }
  }
];

const text = $input.first().json.text; // ambil teks dari node sebelumnya
const chunkSize = 800;
const overlap = 100;
const chunks = [];
for (let i = 0; i < text.length; i += (chunkSize - overlap)) {
  chunks.push({ chunk: text.slice(i, i + chunkSize) });
}
return chunks.map(c => ({ json: c }));
