const text = $input.first().json.text; // ambil teks dari node sebelumnya
const chunkSize = 800;
const overlap = 100;
const chunks = [];
for (let i = 0; i < text.length; i += (chunkSize - overlap)) {
  chunks.push({ chunk: text.slice(i, i + chunkSize) });
}
return chunks.map(c => ({ json: c }));
